mapper.select:
select count(user_id) 
from product_grade
where product_code = #{productCode}

select rpg.user_id, rpg.product_code, rpg.grade, rpg.grade_comment, rpg.grade_date
from
(
	select pg.*, rownum as rnum
	from
	(
		select *
		from product_grade
		where product_code = #{productCode}
		order by grade_date desc
	) pg
) rpg

	where rnum >= #{start} and rnum <= #{end}
